import { Group } from 'three'
import Garnet from '../cast/garnet'
import Sun from '../cast/sun'

class SolarSystem {
    constructor() {
      this.group = new Group();

      // Create instances of Garnet and Sun
      const garnet = new Garnet();
      const sun = new Sun();

      // Set positions for Garnet and Sun
      garnet.group.position.x = -20; // Adjust as needed
      sun.mesh.position.x = 0; // Adjust as needed

      // Add Garnet and Sun to the Solar System group
      this.group.add(garnet);
      this.group.add(sun);
    }

  }

  export default SolarSystem
