/**
 * The scene-manager module serves as an entry point to all scene-related activities, from initialization
 * to access to objects to anything else.
 *
 * The starter version of the scene manager is adapted from the introductory code provided by three.js.
 */
import { Scene, PerspectiveCamera, WebGLRenderer, AmbientLight, DirectionalLight, TextureLoader, RingGeometry, MeshBasicMaterial, Mesh, Camera, CameraHelper } from 'three'
import Garnet from '../cast/garnet'
import Peridot from '../cast/peridot'
import galaxyNight from '../textures/galaxy-night.jpg'

const DEFAULT_ROTATION_RATE = 0.03

class Sun {
  constructor(radius) {
    const sunGeometry = new SphereGeometry(radius, 32, 32);
    const sunMaterial = new MeshPhongMaterial({ color: 'yellow' });
    this.sun = new Mesh(sunGeometry, sunMaterial);
  }
}

const createDemoUniverse = ({ fieldOfView, width, height, nearPlane, farPlane}) => {
  const scene = new Scene()
  const camera = new PerspectiveCamera(fieldOfView, width / height, nearPlane, farPlane)

  const textureLoader = new TextureLoader()
  //scene.background = textureLoader.load(galaxyNight)

  let moveDirection = 1;
  const moveSpeed = 0.01;
  const orbitRadius = 0.05;

  const backgroundRingCamera = new Camera()
  const backgroundRing = new RingGeometry(2)
  const backgroundMaterial = new MeshBasicMaterial({ color: 'brown' })
  const backgroundMesh = new Mesh(backgroundRing, backgroundMaterial)
  scene.add(backgroundMesh)

  const renderer = new WebGLRenderer()
  renderer.setSize(width, height)

  scene.add(new AmbientLight('white', 1.5))

  const directionalLight = new DirectionalLight('white', 1)
  directionalLight.position.set(-1.5, 1, 2)
  directionalLight.target.position.set(0, 0, 0)
  scene.add(directionalLight)
  scene.add(directionalLight.target)
  directionalLight.castShadow = true;

  directionalLight.shadow.mapSize.width = 1024;
  directionalLight.shadow.mapSize.height = 1024;
  directionalLight.shadow.camera.near = 0.5;
  directionalLight.shadow.camera.far = 500;


  const directionalLightFromBehind = new DirectionalLight('white', 2)
  directionalLightFromBehind.position.set(-1.5, 1, -2)
  directionalLightFromBehind.target.position.set(0, 0, 0)
  scene.add(directionalLightFromBehind)
  scene.add(directionalLightFromBehind.target)

  const shadowHelper = new CameraHelper(directionalLight.shadow.camera);
  scene.add(shadowHelper);

  const garnet = new Garnet('gray')
  scene.add(garnet.mesh)

  const peridot = new Peridot()
  scene.add(peridot.group)


  // Turning is a universe-specific behavior: you can decide what these can be.
  let turning = false
  const turn = () => {
    turning = true
  }

  const stop = () => {
    turning = false
  }

  const animate = () => {
    window.requestAnimationFrame(animate)

    const angle = DEFAULT_ROTATION_RATE * moveDirection;
    garnet.mesh.position.x = orbitRadius * Math.cos(angle)/2;
    garnet.mesh.position.y = orbitRadius * Math.sin(angle);

    backgroundMesh.rotation.x += DEFAULT_ROTATION_RATE;
    backgroundMesh.rotation.y += DEFAULT_ROTATION_RATE;

    garnet.mesh.rotation.x += DEFAULT_ROTATION_RATE
    garnet.mesh.rotation.y += DEFAULT_ROTATION_RATE

    if (turning) {


      peridot.group.rotation.x -= DEFAULT_ROTATION_RATE / 2
      peridot.group.rotation.y -= DEFAULT_ROTATION_RATE / 4
    }


    scene.position.x += moveSpeed * moveDirection;

    // Check if the scene is out of FOV and change direction
    if (scene.position.x > 5 || scene.position.x < -5) {
      moveDirection *= -1; // Change direction
    }

    renderer.render(scene, backgroundRingCamera);
    renderer.render(scene, camera)
  }

  return {
    camera,
    renderer,
    animate,
    turn,
    stop,
    cast: {
      garnet,
      peridot
    }
  }
}

export { createDemoUniverse }
