import { SphereGeometry, MeshPhongMaterial, Mesh, Scene } from 'three';

class Sun {
  constructor() {
    const sunRadius = 1
    const sunGeometry = new SphereGeometry(sunRadius, 32, 32);
    const sunMaterial = new MeshPhongMaterial({ color: 'yellow' });
    this.sun = new Mesh(sunGeometry, sunMaterial);
    this.orbitingObjects = []; // Objects that will orbit around the sun

    const flameGeometry = new SphereGeometry();
    const flameMaterial = new MeshPhongMaterial({color: 'orange'});
    this.flame = new Mesh(flameGeometry, flameMaterial);
    this.scene = new Scene();
    this.scene.add(this.flame)
    this.scene.add(this.sun)

  }}

export default Sun;
